function classToggle(){var e=document.getElementsByTagName("body")[0];if(e.classList)e.classList.toggle("js-menu__opened");else{var s=e.className.split(" "),n=s.indexOf("js-menu__opened");0<=n?s.splice(n,1):s.push("js-menu__opened"),e.className=s.join(" ")}}document.getElementById("js-menu-burger").addEventListener("click",classToggle);